import React, { useState, useEffect } from 'react';
import { orderAPI } from '../services/api';

const OrderList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [updatingStatus, setUpdatingStatus] = useState(null);

  const fetchOrders = async () => {
    try {
      console.log('ðŸ”„ Fetching orders...');
      const response = await orderAPI.getAllOrders();
      console.log('âœ… Orders fetched:', response.data.orders);
      
      // Ensure all orders have proper priority values
      const ordersWithSafePriority = (response.data.orders || []).map(order => ({
        ...order,
        priority: order.priority || 'Medium' // Default to 'Medium' if null/undefined
      }));
      
      setOrders(ordersWithSafePriority);
    } catch (error) {
      console.error('âŒ Error fetching orders:', error);
      setOrders([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const updateOrderStatus = async (orderId, newStatus) => {
    setUpdatingStatus(orderId);
    try {
      await orderAPI.updateOrderStatus(orderId, newStatus);
      console.log(`âœ… Order ${orderId} status updated to ${newStatus}`);
      fetchOrders(); // Refresh the list
    } catch (error) {
      console.error('âŒ Error updating order status:', error);
    } finally {
      setUpdatingStatus(null);
    }
  };

  // FIXED: Completely safe priority class function
  const getPriorityClass = (priority) => {
    // Handle null, undefined, empty string, or invalid values
    if (!priority || typeof priority !== 'string') {
      return 'priority-medium';
    }
    
    const priorityLower = priority.toLowerCase().trim();
    const priorityClass = {
      'low': 'priority-low',
      'medium': 'priority-medium', 
      'high': 'priority-high'
    };
    
    return priorityClass[priorityLower] || 'priority-medium';
  };

  // FIXED: Completely safe status class function
  const getStatusClass = (status) => {
    // Handle null, undefined, empty string, or invalid values
    if (!status || typeof status !== 'string') {
      return 'status-received';
    }
    
    const statusLower = status.toLowerCase().trim();
    const statusClass = {
      'received': 'status-received',
      'in progress': 'status-in-progress',
      'completed': 'status-completed',
      'delivered': 'status-delivered'
    };
    
    return statusClass[statusLower] || 'status-received';
  };

  // Safe value display function
  const safeDisplay = (value, defaultValue = 'N/A') => {
    return value || defaultValue;
  };

  if (loading) {
    return (
      <div className="card">
        <div className="loading">
          <div className="loading-spinner"></div>
          Loading orders...
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="card-title">ðŸ“¦ Order List</h2>
        <button 
          onClick={fetchOrders}
          className="btn btn-outline"
          disabled={loading}
        >
          ðŸ”„ Refresh
        </button>
      </div>

      {orders.length === 0 ? (
        <div className="empty-state">
          <div className="empty-state-icon">ðŸ“¦</div>
          <h3>No orders found</h3>
          <p>Create your first order to get started</p>
        </div>
      ) : (
        <div className="table-container responsive-table">
          <table className="table">
            <thead>
              <tr>
                <th style={{ width: '80px' }}>Order ID</th>
                <th style={{ width: '150px' }}>Customer</th>
                <th style={{ width: '120px' }}>Service</th>
                <th style={{ width: '80px' }}>Qty</th>
                <th style={{ width: '100px' }}>Amount</th>
                <th style={{ width: '120px' }}>Status</th>
                <th style={{ width: '100px' }}>Priority</th>
                <th style={{ width: '120px' }}>Created</th>
                <th style={{ width: '150px' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order.id}>
                  <td style={{ fontWeight: 'bold' }}>#{safeDisplay(order.id)}</td>
                  <td>
                    <div>
                      <strong>{safeDisplay(order.customer_name)}</strong>
                      <br />
                      <small style={{ color: 'var(--gray-600)' }}>
                        {safeDisplay(order.customer_phone)}
                      </small>
                    </div>
                  </td>
                  <td>{safeDisplay(order.service_name)}</td>
                  <td style={{ textAlign: 'center' }}>{safeDisplay(order.quantity)}</td>
                  <td style={{ fontWeight: 'bold', color: 'var(--success)' }}>
                    â‚¹{safeDisplay(order.total_amount, '0.00')}
                  </td>
                  <td>
                    <span className={`status-badge ${getStatusClass(order.status)}`}>
                      {safeDisplay(order.status, 'Received')}
                    </span>
                  </td>
                  <td>
                    <span className={`priority-badge ${getPriorityClass(order.priority)}`}>
                      {safeDisplay(order.priority, 'Medium')}
                    </span>
                  </td>
                  <td style={{ fontSize: '0.875rem', color: 'var(--gray-600)' }}>
                    {order.created_date ? new Date(order.created_date).toLocaleDateString('en-IN') : 'N/A'}
                  </td>
                  <td>
                    <select
                      value={safeDisplay(order.status, 'Received')}
                      onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                      className="form-select"
                      style={{ 
                        padding: '0.25rem', 
                        fontSize: '0.8rem',
                        width: '100%'
                      }}
                      disabled={updatingStatus === order.id}
                    >
                      <option value="Received">Received</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Delivered">Delivered</option>
                    </select>
                    {updatingStatus === order.id && (
                      <div style={{ fontSize: '0.7rem', color: 'var(--gray-500)', marginTop: '0.25rem' }}>
                        Updating...
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      
      {/* Footer */}
      <div style={{ 
        padding: '1rem 1.5rem', 
        textAlign: 'center', 
        color: 'var(--gray-600)', 
        fontSize: '0.875rem',
        borderTop: '1px solid var(--gray-200)',
        background: 'var(--gray-50)'
      }}>
        ðŸ“Š Showing {orders.length} order{orders.length !== 1 ? 's' : ''}
      </div>
    </div>
  );
};

export default OrderList;
