import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// Enhanced request interceptor
api.interceptors.request.use(
  (config) => {
    console.log(`ðŸ”„ ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('âŒ Request error:', error);
    return Promise.reject(error);
  }
);

// Enhanced response interceptor with better error handling
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    let errorMessage = 'An unexpected error occurred';
    
    if (error.code === 'ECONNREFUSED') {
      errorMessage = 'Cannot connect to server. Please make sure the backend is running on port 5000.';
    } else if (error.response) {
      // Server responded with error status
      errorMessage = error.response.data?.error || error.response.data?.message || `Server error: ${error.response.status}`;
    } else if (error.request) {
      // Request made but no response received
      errorMessage = 'No response from server. Please check your connection.';
    }
    
    console.error('âŒ API Error:', {
      message: errorMessage,
      url: error.config?.url,
      status: error.response?.status,
      code: error.code
    });
    
    // Create a enhanced error object
    const enhancedError = new Error(errorMessage);
    enhancedError.status = error.response?.status;
    enhancedError.code = error.code;
    enhancedError.originalError = error;
    
    return Promise.reject(enhancedError);
  }
);

export const customerAPI = {
  addCustomer: (customerData) => api.post('/customers/add', customerData),
  getAllCustomers: () => api.get('/customers/all'),
};

export const orderAPI = {
  createOrder: (orderData) => api.post('/orders/create', orderData),
  getAllOrders: () => api.get('/orders/all'),
  updateOrderStatus: (orderId, status) => 
    api.put(`/orders/update-status/${orderId}`, { status }),
};

export default api;
