import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import Customers from './pages/Customers';
import Orders from './pages/Orders';
import Dashboard from './pages/Dashboard';
import AnimatedLogin from './components/AnimatedLogin';
import { authAPI } from './services/api';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const response = await authAPI.verifyToken();
        setUser(response.data.user);
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('token');
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = (userData, token) => {
    setUser(userData);
    localStorage.setItem('token', token);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('token');
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loading-spinner"></div>
        <p>Loading Laundry Management System...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="auth-container">
        <AnimatedLogin onLogin={handleLogin} />
      </div>
    );
  }

  return (
    <Router>
      <div className="app">
        <nav className="navbar">
          <div className="nav-brand">
            <h1>ðŸ§º Laundry Management System</h1>
            <span className="user-info">
              Welcome, {user.username} ({user.role})
            </span>
          </div>
          <div className="nav-links">
            <Link 
              to="/" 
              className={activeTab === 'dashboard' ? 'nav-link active' : 'nav-link'}
              onClick={() => setActiveTab('dashboard')}
            >
              ðŸ“Š Dashboard
            </Link>
            <Link 
              to="/customers" 
              className={activeTab === 'customers' ? 'nav-link active' : 'nav-link'}
              onClick={() => setActiveTab('customers')}
            >
              ðŸ‘¥ Customers
            </Link>
            <Link 
              to="/orders" 
              className={activeTab === 'orders' ? 'nav-link active' : 'nav-link'}
              onClick={() => setActiveTab('orders')}
            >
              ðŸ“¦ Orders
            </Link>
            <button 
              onClick={handleLogout}
              className="nav-link logout-btn"
            >
              ðŸšª Logout
            </button>
          </div>
        </nav>

        <main className="main-content">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/customers" element={<Customers />} />
            <Route path="/orders" element={<Orders />} />
            <Route path="/login" element={<Navigate to="/" />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
