import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// Add auth header to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Handle auth errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.reload();
    }
    return Promise.reject(error);
  }
);

// Enhanced request logging
api.interceptors.request.use(
  (config) => {
    console.log(`ðŸ”„ ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('âŒ Request error:', error);
    return Promise.reject(error);
  }
);

// Enhanced response logging
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('âŒ API Error:', {
      message: error.message,
      url: error.config?.url,
      status: error.response?.status,
      data: error.response?.data
    });
    
    if (error.code === 'ECONNREFUSED') {
      console.error('ðŸ’¡ Backend server is not running. Please start the backend server on port 5000.');
    }
    
    return Promise.reject(error);
  }
);

export const customerAPI = {
  addCustomer: (customerData) => api.post('/customers/add', customerData),
  getAllCustomers: () => api.get('/customers/all'),
  getCustomerStats: () => api.get('/customers/stats'),
};

export const orderAPI = {
  createOrder: (orderData) => api.post('/orders/create', orderData),
  getAllOrders: () => api.get('/orders/all'),
  updateOrderStatus: (orderId, status) => 
    api.put(`/orders/update-status/${orderId}`, { status }),
  getOrderStats: () => api.get('/orders/stats'),
};

export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/register', userData),
  verifyToken: () => api.get('/auth/verify'),
};

export default api;
