import React, { useState } from "react";
import * as Components from "./AuthComponents";
import { authAPI } from "../services/api";

function AnimatedLogin({ onLogin }) {
  const [signIn, setSignIn] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Login form state
  const [loginData, setLoginData] = useState({
    username: "",
    password: ""
  });

  // Register form state
  const [registerData, setRegisterData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    role: "staff"
  });

  const handleLoginChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value
    });
    setError("");
  };

  const handleRegisterChange = (e) => {
    setRegisterData({
      ...registerData,
      [e.target.name]: e.target.value
    });
    setError("");
    setSuccess("");
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const response = await authAPI.login(loginData);
      onLogin(response.data.user, response.data.token);
    } catch (err) {
      setError(err.response?.data?.error || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    // Validation
    if (registerData.password !== registerData.confirmPassword) {
      setError("Passwords do not match");
      setLoading(false);
      return;
    }

    if (registerData.password.length < 6) {
      setError("Password must be at least 6 characters long");
      setLoading(false);
      return;
    }

    try {
      const { confirmPassword, ...submitData } = registerData;
      await authAPI.register(submitData);
      setSuccess("Registration successful! You can now sign in.");
      
      // Reset form
      setRegisterData({
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
        role: "staff"
      });
      
      // Switch to login after successful registration
      setTimeout(() => {
        setSignIn(true);
        setSuccess("");
      }, 2000);
    } catch (err) {
      setError(err.response?.data?.error || "Registration failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const toggleForm = () => {
    setSignIn(!signIn);
    setError("");
    setSuccess("");
  };

  return (
    <Components.Container>
      {/* Sign Up Container */}
      <Components.SignUpContainer signingIn={signIn}>
        <Components.Form onSubmit={handleRegisterSubmit}>
          <Components.Title>Create Account</Components.Title>
          
          <Components.Input 
            type="text" 
            name="username"
            placeholder="Username" 
            value={registerData.username}
            onChange={handleRegisterChange}
            required
          />
          
          <Components.Input 
            type="email" 
            name="email"
            placeholder="Email" 
            value={registerData.email}
            onChange={handleRegisterChange}
            required
          />
          
          <Components.Input 
            type="password" 
            name="password"
            placeholder="Password" 
            value={registerData.password}
            onChange={handleRegisterChange}
            required
          />
          
          <Components.Input 
            type="password" 
            name="confirmPassword"
            placeholder="Confirm Password" 
            value={registerData.confirmPassword}
            onChange={handleRegisterChange}
            required
          />
          
          <Components.Select 
            name="role"
            value={registerData.role}
            onChange={handleRegisterChange}
          >
            <option value="staff">Staff</option>
            <option value="admin">Admin</option>
          </Components.Select>

          {error && <Components.ErrorMessage>{error}</Components.ErrorMessage>}
          {success && <Components.SuccessMessage>{success}</Components.SuccessMessage>}

          <Components.Button type="submit" disabled={loading}>
            {loading && <Components.LoadingSpinner />}
            {loading ? "Signing Up..." : "Sign Up"}
          </Components.Button>
        </Components.Form>
      </Components.SignUpContainer>

      {/* Sign In Container */}
      <Components.SignInContainer signingIn={signIn}>
        <Components.Form onSubmit={handleLoginSubmit}>
          <Components.Title>Sign in</Components.Title>
          
          <Components.Input 
            type="text" 
            name="username"
            placeholder="Username" 
            value={loginData.username}
            onChange={handleLoginChange}
            required
          />
          
          <Components.Input 
            type="password" 
            name="password"
            placeholder="Password" 
            value={loginData.password}
            onChange={handleLoginChange}
            required
          />
          
          <Components.Anchor href="#" onClick={() => alert("Please contact administrator to reset your password.")}>
            Forgot your password?
          </Components.Anchor>

          {error && <Components.ErrorMessage>{error}</Components.ErrorMessage>}

          <Components.Button type="submit" disabled={loading}>
            {loading && <Components.LoadingSpinner />}
            {loading ? "Signing In..." : "Sign In"}
          </Components.Button>

          <div style={{ marginTop: '20px', fontSize: '12px', color: '#666' }}>
            <strong>Demo Credentials:</strong><br />
            Username: <code>admin</code><br />
            Password: <code>admin123</code>
          </div>
        </Components.Form>
      </Components.SignInContainer>

      {/* Overlay Container */}
      <Components.OverlayContainer signingIn={signIn}>
        <Components.Overlay signingIn={signIn}>
          <Components.LeftOverlayPanel signingIn={signIn}>
            <Components.Title>Welcome Back!</Components.Title>
            <Components.Paragraph>
              To manage your laundry business, please login with your credentials
            </Components.Paragraph>
            <Components.GhostButton onClick={toggleForm}>
              Sign In
            </Components.GhostButton>
          </Components.LeftOverlayPanel>

          <Components.RightOverlayPanel signingIn={signIn}>
            <Components.Title>Hello, Friend!</Components.Title>
            <Components.Paragraph>
              Enter your personal details and start managing your laundry business with us
            </Components.Paragraph>
            <Components.GhostButton onClick={toggleForm}>
              Sign Up
            </Components.GhostButton>
          </Components.RightOverlayPanel>
        </Components.Overlay>
      </Components.OverlayContainer>
    </Components.Container>
  );
}

export default AnimatedLogin;
