import React, { useState, useEffect } from 'react';
import { orderAPI } from '../services/api';

const OrderList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [updateLoading, setUpdateLoading] = useState({});

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await orderAPI.getAllOrders();
      console.log('Fetched orders:', response.data.orders);
      setOrders(response.data.orders);
    } catch (error) {
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const updateStatus = async (orderId, newStatus) => {
    try {
      setUpdateLoading(prev => ({ ...prev, [orderId]: true }));
      console.log(`Updating order ${orderId} to status: ${newStatus}`);
      
      await orderAPI.updateOrderStatus(orderId, newStatus);
      
      // Update local state immediately for better UX
      setOrders(prevOrders => 
        prevOrders.map(order => 
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
      
      console.log(`Order ${orderId} status updated successfully to ${newStatus}`);
    } catch (error) {
      console.error('Error updating order status:', error);
      alert('Failed to update order status. Please try again.');
      // Refresh to get actual state
      fetchOrders();
    } finally {
      setUpdateLoading(prev => ({ ...prev, [orderId]: false }));
    }
  };

  const getStatusClass = (status) => {
    return `status-badge status-${status.toLowerCase().replace(' ', '-')}`;
  };

  const getPriorityClass = (priority) => {
    return `priority-badge priority-${priority.toLowerCase()}`;
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount || 0);
  };

  // Filter orders based on status and search term
  const filteredOrders = orders.filter(order => {
    const matchesFilter = filter === 'all' || order.status === filter;
    const matchesSearch = order.customer_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.service_name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  const statusCounts = {
    all: orders.length,
    Received: orders.filter(o => o.status === 'Received').length,
    'In Progress': orders.filter(o => o.status === 'In Progress').length,
    Completed: orders.filter(o => o.status === 'Completed').length,
    Delivered: orders.filter(o => o.status === 'Delivered').length
  };

  if (loading) {
    return (
      <div className="card">
        <div className="loading">
          <div className="loading-spinner"></div>
          Loading orders...
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="card-title">üì¶ Order Management</h2>
        <button 
          onClick={fetchOrders}
          className="btn btn-outline"
          disabled={loading}
        >
          üîÑ Refresh
        </button>
      </div>

      {/* Filters and Search */}
      <div className="grid grid-2" style={{ marginBottom: '2rem', gap: '1rem' }}>
        <div className="form-group">
          <label className="form-label">üîç Search Orders</label>
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="form-input"
            placeholder="Search by customer or service..."
          />
        </div>

        <div className="form-group">
          <label className="form-label">üìä Filter by Status</label>
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="form-select"
          >
            <option value="all">All Orders ({statusCounts.all})</option>
            <option value="Received">Received ({statusCounts.Received})</option>
            <option value="In Progress">In Progress ({statusCounts['In Progress']})</option>
            <option value="Completed">Completed ({statusCounts.Completed})</option>
            <option value="Delivered">Delivered ({statusCounts.Delivered})</option>
          </select>
        </div>
      </div>

      {filteredOrders.length === 0 ? (
        <div className="empty-state">
          <div className="empty-state-icon">üì¶</div>
          <h3>No orders found</h3>
          <p>{searchTerm || filter !== 'all' ? 'Try changing your search or filter' : 'Create your first order to get started'}</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Customer</th>
                <th>Service</th>
                <th>Quantity</th>
                <th>Amount</th>
                <th>Priority</th>
                <th>Status</th>
                <th>Created</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.map((order) => (
                <tr key={order.id}>
                  <td>
                    <strong>#{order.id}</strong>
                  </td>
                  <td>
                    <div>
                      <strong>{order.customer_name}</strong>
                      <br />
                      <small style={{ color: 'var(--gray-600)' }}>{order.customer_phone}</small>
                    </div>
                  </td>
                  <td>{order.service_name}</td>
                  <td>{order.quantity}</td>
                  <td>
                    <strong>{formatCurrency(order.total_amount)}</strong>
                  </td>
                  <td>
                    <span className={getPriorityClass(order.priority)}>
                      {order.priority}
                    </span>
                  </td>
                  <td>
                    <span className={getStatusClass(order.status)}>
                      {order.status}
                    </span>
                  </td>
                  <td>
                    {new Date(order.created_date).toLocaleDateString()}
                    <br />
                    <small style={{ color: 'var(--gray-600)' }}>
                      {new Date(order.created_date).toLocaleTimeString()}
                    </small>
                  </td>
                  <td>
                    <select
                      value={order.status}
                      onChange={(e) => updateStatus(order.id, e.target.value)}
                      className="form-select"
                      style={{ 
                        padding: '0.5rem',
                        fontSize: '0.875rem',
                        border: '1px solid var(--gray-300)',
                        borderRadius: '6px',
                        minWidth: '120px'
                      }}
                      disabled={updateLoading[order.id]}
                    >
                      <option value="Received">Received</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Delivered">Delivered</option>
                    </select>
                    {updateLoading[order.id] && (
                      <div style={{ marginTop: '0.5rem' }}>
                        <div className="loading-spinner" style={{ width: '16px', height: '16px' }}></div>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Summary Stats */}
      <div className="grid grid-4" style={{ marginTop: '2rem' }}>
        <div className="stat-card" style={{ padding: '1rem' }}>
          <div className="stat-value" style={{ fontSize: '1.5rem' }}>{statusCounts.Received}</div>
          <div className="stat-label">Received</div>
        </div>
        <div className="stat-card" style={{ padding: '1rem' }}>
          <div className="stat-value" style={{ fontSize: '1.5rem' }}>{statusCounts['In Progress']}</div>
          <div className="stat-label">In Progress</div>
        </div>
        <div className="stat-card" style={{ padding: '1rem' }}>
          <div className="stat-value" style={{ fontSize: '1.5rem' }}>{statusCounts.Completed}</div>
          <div className="stat-label">Completed</div>
        </div>
        <div className="stat-card" style={{ padding: '1rem' }}>
          <div className="stat-value" style={{ fontSize: '1.5rem' }}>{statusCounts.Delivered}</div>
          <div className="stat-label">Delivered</div>
        </div>
      </div>
    </div>
  );
};

export default OrderList;
