import React, { useState, useEffect } from 'react';
import { customerAPI, orderAPI } from '../services/api';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalCustomers: 0,
    totalOrders: 0,
    pendingOrders: 0,
    completedOrders: 0
  });

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const [customersRes, ordersRes] = await Promise.all([
        customerAPI.getAllCustomers(),
        orderAPI.getAllOrders()
      ]);

      const customers = customersRes.data.customers;
      const orders = ordersRes.data.orders;

      setStats({
        totalCustomers: customers.length,
        totalOrders: orders.length,
        pendingOrders: orders.filter(order => 
          order.status === 'Received' || order.status === 'In Progress'
        ).length,
        completedOrders: orders.filter(order => 
          order.status === 'Completed' || order.status === 'Delivered'
        ).length
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <div className="grid grid-2">
        <div className="card">
          <h3>Total Customers</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#667eea' }}>
            {stats.totalCustomers}
          </p>
        </div>
        <div className="card">
          <h3>Total Orders</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#28a745' }}>
            {stats.totalOrders}
          </p>
        </div>
        <div className="card">
          <h3>Pending Orders</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#ffc107' }}>
            {stats.pendingOrders}
          </p>
        </div>
        <div className="card">
          <h3>Completed Orders</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#17a2b8' }}>
            {stats.completedOrders}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
